class Info {
    private $dsn = "mysql:dbname=tests;host=127.0.0.1";
    private $user = 'root';
    private $password = '';
    private $db;
    private $ip;
    private $agent;
    private $url;

    const DEFAULT_VIEW_COUNT = 1;
    const UNIQUE_WHERE_CONDITION = ' WHERE (ip_address = :ip_address AND user_agent = :user_agent AND page_url = :page_url)';

    public function __construct($ip, $agent, $url)
    {
        $this->ip = $ip;
        $this->agent = $agent;
        $this->url = $url;
        $this->db = new PDO($this->dsn, $this->user, $this->password);
    }

    public function insertOrUpdate()
    {
        if ($this->getExists()) {
            $this->update();
        } else {
            $this->create();
        }
    }

    private function getExists()
    {
        $sql = "SELECT * FROM info" . self::UNIQUE_WHERE_CONDITION;
        $query = $this->db->prepare($sql);
        $query->execute($this->getWhereConditionArray());
        return $query->fetch();
    }

    private function create()
    {
        if ($this->ip && $this->agent && $this->url) {
            $sql = "INSERT INTO info (`ip_address`, `user_agent`, `page_url`, `views_count`) 
                    VALUES (?,?,?,?)";
            $query = $this->db->prepare($sql);
            $query->execute($this->getNewInfoData());
        }
    }

    private function update()
    {
        $sql = "UPDATE info SET views_count = views_count + " .
            self::DEFAULT_VIEW_COUNT . self::UNIQUE_WHERE_CONDITION;
        $query = $this->db->prepare($sql);
        $query->execute($this->getWhereConditionArray());
    }

    private function getWhereConditionArray()
    {
        return [':ip_address' => $this->ip, ':user_agent' => $this->agent, ':page_url' => $this->url];
    }

    private function getNewInfoData()
    {
        return [
            $this->ip,
            $this->agent,
            $this->url,
            self::DEFAULT_VIEW_COUNT
        ];
    }
}

$ip = $_SERVER['REMOTE_ADDR'];
$agent = $_SERVER['HTTP_USER_AGENT'];
$url = $_SERVER['HTTP_REFERER'];

(new Info($ip, $agent, $url))->insertOrUpdate();

